// ===============================
// PRISMA:ORIENT ‚Äî Situation Snapshot
// ===============================

const evaluationTs = $node["evaluation_ts"].json.evaluation_ts;

if (!evaluationTs) {
  throw new Error("evaluation_ts (run_id) not found");
}

const WINDOW_MINUTES = 60;
const windowEndTs = new Date().toISOString();
const windowStartTs = new Date(Date.now() - WINDOW_MINUTES * 60 * 1000).toISOString();

const latestStateItems = $items("Get latest state_now") || [];
const latestState = latestStateItems.map(i => i.json);

const recentEventItems = $items("Get all events  60 minutes before_now") || [];
const recentEvents = recentEventItems.map(i => i.json);

// üÜï Extraer el ts sint√©tico m√°s reciente de los eventos
const scenarioTs = recentEvents.length > 0 
  ? recentEvents.reduce((max, e) => e.ts > max ? e.ts : max, recentEvents[0].ts)
  : null;

const entities = latestState.map(state => {
  const events = recentEvents.filter(e => e.entity_id === state.entity_id);
  events.sort((a, b) => new Date(a.ts) - new Date(b.ts));

  const firstSeen = events.length > 0 ? events[0].ts : null;
  const lastSeen = events.length > 0 ? events[events.length - 1].ts : null;

  return {
    entity_id: state.entity_id,
    entity_type: state.entity_type,
    latest_ts: state.ts,
    recent_activity: {
      event_count: events.length,
      first_seen: firstSeen,
      last_seen: lastSeen,
    },
    attributes: state.attributes,
  };
});

const context = {
  run_id: evaluationTs,
  total_events: recentEvents.length,
  distinct_entities: new Set(recentEvents.map(e => e.entity_id)).size,
};

return [
  {
    json: {
      snapshot_ts: evaluationTs,
      scenario_ts: scenarioTs,  // üÜï timestamp sint√©tico
      snapshot_mode: "run_based",
      window: {
        type: "last_60_event_minutes",
        start: windowStartTs,
        end: windowEndTs,
      },
      entities,
      context,
    },
  },
];

if (!items || items.length === 0) {
  throw new Error("No data from HTTP Request");
}

return [{
  json: {
    evaluation_ts: items[0].json.run_id
  }
}];

const snapshot = items[0].json;

if (!snapshot || !snapshot.entities) {
  throw new Error("Snapshot not found or invalid");
}

const decision_input = {
  snapshot_ts: snapshot.snapshot_ts,
  scenario_ts: snapshot.scenario_ts,  // üÜï
  entities: snapshot.entities.map(e => ({
    entity_id: e.entity_id,
    entity_type: e.entity_type,
    attributes: e.attributes,
  })),
};

const meta = {
  entity_count: decision_input.entities.length,
};

return [{ json: { decision_input, meta } }];

const text = $input.first().json.text || $input.first().json.output || $input.first().json.content;
const match = text.match(/\{[\s\S]*\}/);
const parsed = JSON.parse(match[0]);
return parsed;
